const NavMenu = () => {
  const router = useRouter();
  return (
    <ul className={"menu"}>
      <li className={"menu-item"} onClick={() => router.push('/')}>Movies</li>
      <li className={"menu-item"} onClick={() => router.push('/search')}>Search</li>
      <li className={"menu-item"} onClick={() => router.push('/posts')}>Blog</li>
      <li className={"menu-item"} onClick={() => router.push('/about')}>About</li>
    </ul>
  );
};

const NavButton = () => {
  const { theme, setTheme } = useTheme();
  const toggleTheme = () => setTheme(theme === "dark" ? "light" : "dark");
  return (
    <button className={"nav-btn"} onClick={toggleTheme}>
      Theme
    </button>
  );
};
